// String t is generated by random shuffling string s and then add one more letter at a random position. Find the letter that was added in t.

let findTheDifference = (s, t) => {
    let sArr = [...s]
    for (letter of sArr) 
        t = t.replace(letter, '')
    return t
}

console.log(findTheDifference('a', 'aa'))